import './App.css';
import React, { useState, useEffect } from "react";
import Select from "react-select";
import "bootstrap/dist/css/bootstrap.css";

 async function postData(url = "", data = {}) {
  // Default options are marked with *
  const response = await fetch(url, {
    method: "POST", // *GET, POST, PUT, DELETE, etc.
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "same-origin", // include, *same-origin, omit
    headers: {
      "Content-Type": "application/json",
      // 'Content-Type': 'application/x-www-form-urlencoded',
    },
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
    body: JSON.stringify(data), // body data type must match "Content-Type" header
  });
  return response.json(); // parses JSON response into native JavaScript objects
}

function App() {
  const baseURL = "http://localhost:8000/";

  const [branches, setBranches] = useState("");

  useEffect(() => {
    fetch(`${baseURL}branches`)
      .then((res) => res.json())
      .then((data) => {setBranches(data);}
      );
  }, []);

  const [inventory, setInventory] = useState("");

  const showInventory = (selectedOption) => {
    const movies = postData(`${baseURL}inventory`, {branch: selectedOption}).then((results) => {
      return results;
    });

    let movieTable = "<table><tr><th>Movie</th><th>Director</th><th>Price</th><th>Inventory on Hand</th></tr>"; 
    const loadMovies = async () => {
      const m = await movies;
      m.map((movie)=> {
        movieTable += "<tr>";
        movieTable += "<td>"+movie.Title+"</td>";
        movieTable += "<td>"+movie.DirectorFirst+"</td>";
        movieTable += "<td>"+movie.Price+"</td>";
        movieTable += "<td>"+movie.OnHand+"</td>";
        movieTable += "</tr>";
      });

      movieTable += "</table>"
      setInventory(movieTable);
    };

    loadMovies();

  };


  return (
    <div className="App">
      <header className="App-header">
      <h1>Video4Ever Branch Inventory</h1>
      <p>Choose a branch to view the branch inventory.</p>
        <div className="container">
      <div className="mt-5 m-auto w-50">
      <Select options={branches} onChange={showInventory} autoFocus={true} />

      <div className="inventory">
      <table>
        <tbody>
          <tr>
            <th>Name</th>
            <th>Brand</th>
            <th>Image</th>
            <th>Price</th>
            <th>Rating</th>
          </tr>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.title}</td>
              <td>{item.brand}</td>
              <td>
                <img src={item.thumbnail} alt="" height={100} />
              </td>
              <td>{item.price}</td>
              <td>{item.rating}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
        </div></div>
      </header>

    </div>
  );
}

export default App;
