import './App.css';
import React, { useState, useEffect } from "react";

const [branches, setBranches] = useState("");

  useEffect(() => {
    fetch("http://localhost:8000/branches")
      .then((res) => res.json())
      .then((data) => 
        {
          setBranches(
            data.map((branch) => (

                  <option value="{branch.BranhNum}">{branch.BranchName}</option>

            )));
        }
      );
  }, []);

var BranchSelect = React.createClass({


  getInitialState: function() {
      return {
          value: 'select'
      }
  },
  change: function(event){
      this.setState({value: event.target.value});
  },
  render: function(){


     return(
        <div>
            <select name="branches">{branches}</select>
            <p>{this.state.value}</p>
        </div>
     );
  }
});

function App() {


  return (
    <div className="App">
      <header className="App-header">
      <h1>Video4Ever Branch Inventory</h1>
        <p>
         Choose a branch to view the branch inventory.
        </p>
        <BranchSelect/>
      </header>
    </div>
  );
}

export default App;
